CREATE SCHEMA `vacations-app` ;

CREATE TABLE `vacations-app`.`vacations` (
  `id` int NOT NULL AUTO_INCREMENT,
  `description` longtext NOT NULL,
  `destination` varchar(45) NOT NULL,
  `image` varchar(500) NOT NULL DEFAULT '...',
  `depart_date` date DEFAULT NULL,
  `return_date` date DEFAULT NULL,
  `price` INT UNSIGNED NOT NULL DEFAULT '25',
  `is_following` BOOLEAN NOT NULL DEFAULT 0,
  `stops` BOOLEAN NOT NULL DEFAULT 0,
  `followers` int UNSIGNED NOT NULL DEFAULT 0 ,
  `category` varchar(45) NOT NULL,
  `airport` varchar(45) NOT NULL,
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
);


CREATE TABLE `vacations-app`.`users` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `user_name` VARCHAR(45) NOT NULL,
  `first_name` VARCHAR(45) NULL,
  `last_name` VARCHAR(45) NULL,
  `password` VARCHAR(100) NOT NULL,
  `theme` VARCHAR(45) NOT NULL DEFAULT 'light',
  `is_admin` BOOLEAN NOT NULL DEFAULT 0,
  `created_at` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP(),
  `updated_at` DATETIME NULL,
  PRIMARY KEY (`id`));



